} impl From<Polygon> for Shape { fn from(poly: Polygon) -> Self { Shape::Polygon(poly) }
} impl From<Circle> for Shape { fn from(circle: Circle) -> Self { Shape::Circle(circle) }
} impl Shape { pub fn perimeter(&self) -> f64 { match self { Shape::Polygon(poly) => poly.length(), Shape::Circle(circle) => circle.circumference(), } }
