下面是本节的所有代码。 use std::ops::Deref; impl<T> Arc<T> { pub fn new(data: T) -> Arc<T> { // 当前的指针就是第一个引用，因此初始时设置 count 为 1 let boxed = Box::new(ArcInner { rc: AtomicUsize::new(1), data, }); Arc { // 我们从 Box::into_raw 得到该指针，因此使用 `.unwrap()` 是完全可行的 ptr: NonNull::new(Box::into_raw(boxed)).unwrap(), phantom: PhantomData, } }
} unsafe impl<T: Sync + Send> Send for Arc<T> {}
unsafe impl<T: Sync + Send> Sync for Arc<T> {} impl<T> Deref for Arc<T> { type Target = T; fn deref(&self) -> &T { let inner = unsafe { self.ptr.as_ref() }; &inner.data }
}

