[gloo]是一个模块化的工具，使用Rust/WASM构建快速、可靠的Web应用。 use gloo::{events::EventListener, timers::callback::Timeout};
use wasm_bindgen::prelude::*; pub struct DelayedHelloButton { button: web_sys::Element, on_click: events::EventListener,
} impl DelayedHelloButton { pub fn new(document: &web_sys::Document) -> Result<DelayedHelloButton, JsValue> { // 创建 `<button>` 元素. let button = document.create_element("button")?; // 监听button上的`click`事件 let button2 = button.clone(); let on_click = EventListener::new(&button, "click", move |_event| { // 一秒后，更新button中的文本 let button3 = button2.clone(); Timeout::new(1_000, move || { button3.set_text_content(Some("Hello from one second ago!")); }) .forget(); }); Ok(DelayedHelloButton { button, on_click }) }
}

